mapper.select:
select a.id,a.`name` as userName,b.real_name as realName,a.mobile, b.user_type as userType,c.id as roleId,c.`name` as roleName,d.group_id as groupId, d.org_id as orgId,d.farm_id as farmId,b.status from parana_users a join doctor_user_subs b on a.id=b.user_id left join doctor_staffs d on b.user_id=d.user_id join doctor_sub_roles c on b.role_id=c.id where (b.user_type=1 or b.user_type=2) and (c.role_type=0 or c.role_type=1 or c.role_type=2)
and a.id like CONCAT(CONCAT('%',#{userId}),'%')
and a.`name` like CONCAT(CONCAT('%',#{userName}),'%')
and a.mobile like CONCAT(CONCAT('%',#{mobile}),'%')
and b.user_type = #{userType}
order by a.id desc limit ${pageNo},${pageSize}

mapper.update:
update doctor_user_subs set real_name = #{realName},role_id = #{roleId},user_type=#{userType},updated_at=now(),
role_name=#{roleName},status = #{status},contact=#{mobile},user_name=#{userName}
where user_id = #{userId}

update doctor_staffs set group_id = #{groupId},

org_id = #{orgId},

updated_at=now()

    ,mobile = #{mobile}


    ,status = #{status}

where user_id = #{userId}

mapper.select:
select org_id from doctor_farms
where id in

    #{id}

select parent_id from doctor_orgs
where id in

    #{id}

select id from doctor_barns
where farm_id in

    #{id}

mapper.update:
update parana_user_profiles set
realname = #{realName},
updated_at=NOW() where user_id = #{userId}
