mapper.update:
UPDATE

set unit_price=0,amount=0
where org_id=#{orgId} and settlement_date=#{settlementDate}

mapper.select:
select ma.pig_barn_id as pig_barn_id, ma.pig_barn_name as pig_barn_name, ma.pig_type as pig_type, b.staff_name as staff_name, year(mh.settlement_date) as settlement_year, month(mh.settlement_date) as settlement_month, s.`code` as material_code, s.`name` as material_name, db.name as unit, sum(ifnull(round((ma.quantity+ 0.00000001),3),0)+ifnull(round((ma.refund_quantity+ 0.00000001),3),0))+'' as sum_quantity, round(sum(ifnull(round((ma.amount+ 0.00000001),2),0)+ifnull(round((ma.refund_amount+ 0.00000001),2),0))/sum(ifnull(round((ma.quantity+ 0.00000001),3),0)+ifnull(round((ma.refund_quantity+ 0.00000001),3),0)),4)+'' as sum_unit_price, sum(ifnull(round((ma.amount+ 0.00000001),2),0)+ifnull(round((ma.refund_amount+ 0.00000001),2),0))+'' as sum_amount, s.type as material_type, v.short_name as vendor_name, s.specification as specification, f.`name` as farm_name, f.org_id as org_id from doctor_warehouse_material_apply as ma inner join doctor_warehouse_material_handle as mh on ma.material_handle_id=mh.id inner join doctor_barns as b on b.id=ma.pig_barn_id inner join doctor_warehouse_sku as s on s.id=mh.material_id inner join doctor_farms as f on f.id=ma.farm_id inner join doctor_basics as db on db.id=ma.unit inner join doctor_warehouse_vendor as v on v.id=s.vendor_id where ma.apply_type=0
and ma.farm_id = #{farmId}
and year(ma.settlement_date) = #{applyYear}
and month(ma.settlement_date) = #{applyMonth}
and ma.pig_barn_id = #{pigBarnId}
and ma.pig_type = #{pigType}
and ma.type = #{type}
and ma.material_name like concat('%',#{materialName},'%')
group by ma.pig_barn_id,ma.material_id
union all
select '' as pig_barn_id, '合计' as pig_barn_name, '' as pig_type, '' as staff_name, '' as settlement_year, '' as settlement_month, '' as material_code, '' as material_name, '' as unit, '' as sum_quantity, '' as sum_unit_price, ifnull(sum(ifnull(round((ma.amount+ 0.00000001),2),0)+ifnull(round((ma.refund_amount+ 0.00000001),2),0)),0)+'' as sum_amount, '' as material_type, '' as vendor_name, '' as specification, '' as farm_name, '' as org_id from doctor_warehouse_material_apply as ma inner join doctor_warehouse_material_handle as mh on ma.material_handle_id=mh.id inner join doctor_barns as b on b.id=ma.pig_barn_id inner join doctor_warehouse_sku as s on s.id=mh.material_id inner join doctor_farms as f on f.id=ma.farm_id inner join doctor_basics as db on db.id=ma.unit inner join doctor_warehouse_vendor as v on v.id=s.vendor_id where ma.apply_type=0
and ma.farm_id = #{farmId}
and year(ma.settlement_date) = #{applyYear}
and month(ma.settlement_date) = #{applyMonth}
and ma.pig_barn_id = #{pigBarnId}
and ma.pig_type = #{pigType}
and ma.type = #{type}
and ma.material_name like concat('%',#{materialName},'%')
select '' as pig_barn_id, '合计' as pig_barn_name, '' as pig_type, '' as staff_name, '' as settlement_year, '' as settlement_month, '' as material_code, '' as material_name, '' as unit, ifnull(sum(ifnull(round((ma.quantity+ 0.00000001),3),0)+ifnull(round((ma.refund_quantity+ 0.00000001),3),0)),0)+'', '' as sum_unit_price, ifnull(sum(ifnull(round((ma.amount+ 0.00000001),2),0)+ifnull(round((ma.refund_amount+ 0.00000001),2),0)),0)+'' as sum_amount, '' as material_type, '' as vendor_name, '' as specification, '' as farm_name, '' as org_id from doctor_warehouse_material_apply as ma inner join doctor_warehouse_material_handle as mh on ma.material_handle_id=mh.id inner join doctor_barns as b on b.id=ma.pig_barn_id inner join doctor_warehouse_sku as s on s.id=mh.material_id inner join doctor_farms as f on f.id=ma.farm_id inner join doctor_basics as db on db.id=ma.unit inner join doctor_warehouse_vendor as v on v.id=s.vendor_id where ma.apply_type=0
and ma.farm_id = #{farmId}
and year(ma.settlement_date) = #{applyYear}
and month(ma.settlement_date) = #{applyMonth}
and ma.pig_barn_id = #{pigBarnId}
and ma.pig_type = #{pigType}
and ma.type = #{type}
and ma.material_name like concat('%',#{materialName},'%')

select mh.id as id,s.`code` as material_code, s.`name` as material_name, s.type as material_type, ma.warehouse_name as warehouse_name, mh.handle_date as handle_date, year(mh.settlement_date) as settlement_year, month(mh.settlement_date) as settlement_month, mh.type as material_handle_type, ifnull(round((mh.quantity+ 0.00000001),3),0) as quantity, ifnull(round((mh.unit_price+ 0.00000001),4),0) as unit_price, ifnull(round((mh.amount+ 0.00000001),2),0) as amount, ma.pig_barn_name as pig_barn_name, ma.pig_type as pig_type, ifnull(( select w.pig_group_name from doctor_warehouse_material_apply w where w.material_handle_id = ma.material_handle_id and w.apply_type!=0 and w.org_id = ma.org_id limit 1 ),'无') as pig_group_name, b.staff_name as staff_name, f.id as farm_id, f.`name` as farm_name, db.name as unit , v.short_name as vendor_name, s.specification as specification from doctor_warehouse_material_apply as ma inner join doctor_warehouse_material_handle as mh on mh.id=ma.material_handle_id inner join doctor_warehouse_sku as s on s.id=mh.material_id inner join doctor_barns as b on b.id=ma.pig_barn_id inner join doctor_farms as f on f.id=ma.farm_id inner join doctor_basics as db on db.id=mh.unit inner join doctor_warehouse_vendor as v on v.id=s.vendor_id where mh.type=2 and ma.apply_type=0 and mh.delete_flag=1
and ma.pig_barn_id = #{pigBarnId}
and s.`name` like concat('%',#{materialName},'%')
and year(mh.settlement_date) = #{applyYear}
and month(mh.settlement_date) = #{applyMonth}

select mh.id as id,s.`code` as material_code, s.`name` as material_name, s.type as material_type, ma.warehouse_name as warehouse_name, mh.handle_date as handle_date, year(mh.settlement_date) as settlement_year, month(mh.settlement_date) as settlement_month, mh.type as material_handle_type, ifnull(round((mh.quantity+ 0.00000001),3),0) as quantity, ifnull(round((mh.unit_price+ 0.00000001),4),0) as unit_price, ifnull(round((mh.amount+ 0.00000001),2),0) as amount, ma.pig_barn_name as pig_barn_name, ma.pig_type as pig_type, ifnull(( select w.pig_group_name from doctor_warehouse_material_apply w where w.material_handle_id = ma.material_handle_id and w.apply_type!=0 and w.org_id = ma.org_id limit 1 ),'无') as pig_group_name, ma.staff_name as staff_name, f.id as farm_id, f.`name` as farm_name, db.name as unit , v.short_name as vendor_name, s.specification as specification from doctor_warehouse_material_handle as mh inner join doctor_warehouse_sku as s on s.id=mh.material_id inner join doctor_farms as f on f.id=mh.farm_id inner join doctor_basics as db on db.id=mh.unit inner join doctor_warehouse_vendor as v on v.id=s.vendor_id inner join ( select DISTINCT maa.material_handle_id,maa.org_id,maa.warehouse_name,maa.pig_barn_name,maa.pig_type,b.staff_name from doctor_warehouse_material_apply as maa inner join doctor_barns as b on b.id=maa.pig_barn_id where maa.apply_type=0
and maa.pig_barn_id = #{pigBarnId}
and maa.material_handle_id=#{materialHandleId} ) ma on mh.rel_material_handle_id=ma.material_handle_id where mh.delete_flag=1 and mh.type=13
and s.`name` like concat('%',#{materialName},'%')
and year(mh.settlement_date) = #{applyYear}
and month(mh.settlement_date) = #{applyMonth}
and mh.rel_material_handle_id=#{materialHandleId}

SELECT b.id as material_handle_id, c.name as sku_name, c.type as sku_type, a.warehouse_name, date_format( b.handle_date,'%Y-%m-%d') as apply_date, date_format(b.settlement_date,'%Y-%m') as settlement_date, b.type, ifnull(round((b.quantity++ 0.00000001),3),0) as quantity, round(ifnull(round((b.amount++ 0.00000001),2),0)/ifnull(round((b.quantity++ 0.00000001),3),0),4) as unit_price, ifnull(round((b.amount++ 0.00000001),2),0) as amount, a.pig_barn_name, a.pig_type, a.pig_group_name, a.staff_name, f.id as farm_id, f.`name` as farm_name, db.name as unit, v.short_name as vendor_name, c.specification FROM doctor_warehouse_material_handle b JOIN doctor_warehouse_sku c ON b.material_id = c.id JOIN doctor_basics as db on db.id=c.unit JOIN doctor_farms as f on f.id=b.farm_id JOIN doctor_warehouse_vendor as v on v.id=c.vendor_id inner join ( select DISTINCT ma.material_handle_id,ma.warehouse_name,ma.pig_barn_name,ma.pig_type,ma.pig_group_name,bb.staff_name from doctor_warehouse_material_apply as ma inner join doctor_barns as bb on bb.id=ma.pig_barn_id where ma.apply_type=1
and ma.pig_group_id = #{pigGroupId}
and ma.material_handle_id=#{materialHandleId} ) a on b.rel_material_handle_id=a.material_handle_id WHERE b.delete_flag=1 AND b.rel_material_handle_id=#{materialHandleId} AND b.material_id = #{skuId}

SELECT COUNT(1)
from doctor_warehouse_material_apply as ma
where ma.org_id=#{orgId} and  ma.farm_id=#{farmId}
and ma.apply_type=0 
and (ma.apply_type=1 or ma.apply_type=2) 
and ma.apply_date >= #{startDate}
and ma.apply_date <= #{endDate}
and  ma.type=#{materialType}
and ma.material_name like concat('%',#{materialName},'%')
and  ma.pig_type=#{pigType}
and  ma.pig_barn_name like concat('%',#{pigBarnName},'%')
and  ma.pig_group_name like concat('%',#{pigGroupName},'%')

SELECT SUM(ma.quantity+ma.refund_quantity) as sumQuantity ,SUM(ma.amount+ma.refund_amount) as sumAmount
from doctor_warehouse_material_apply as ma
where ma.org_id=#{orgId} and  ma.farm_id=#{farmId}
and ma.apply_type=0 
and (ma.apply_type=1 or ma.apply_type=2) 
and ma.apply_date >= #{startDate}
and ma.apply_date <= #{endDate}
and  ma.type=#{materialType}
and ma.material_name like concat('%',#{materialName},'%')
and  ma.pig_type=#{pigType}
and  ma.pig_barn_name like concat('%',#{pigBarnName},'%')
and  ma.pig_group_name like concat('%',#{pigGroupName},'%')

select DISTINCT ma.type from doctor_warehouse_material_apply as ma
where ma.org_id=#{orgId} and  ma.farm_id=#{farmId}
and ma.apply_date >= #{startDate}
and ma.apply_date <= #{endDate}
and  ma.type=#{materialType}
and ma.material_name like concat('%',#{materialName},'%')
and  ma.pig_type=#{pigType}
and  ma.pig_barn_name like concat('%',#{pigBarnName},'%')
and  ma.pig_group_name like concat('%',#{pigGroupName},'%')
ORDER BY ma.type
