SELECT ifnull(sum( case type when '1' then quantity when '2' then -quantity when '7' then quantity when '8' then -quantity when '9' then quantity when '10' then -quantity when '11' then quantity when '12' then -quantity when '13' then -quantity end ),0) as quantity FROM
WHERE warehouse_id=#{warehouseId} AND material_id=#{skuId}
AND handle_date <= #{handleDate}
AND handle_date < #{handleDate}
AND delete_flag=1 ORDER BY handle_date ASC,id ASC

SELECT

FROM

WHERE org_id=#{orgId}
AND settlement_date=#{settlementDate}
AND delete_flag=1
ORDER BY handle_date ASC,id ASC

SELECT ifnull(sum( case mh.type when '1' then quantity when '2' then -quantity when '7' then quantity when '8' then -quantity when '9' then quantity when '10' then -quantity when '11' then quantity when '12' then -quantity when '13' then -quantity end ),0) as quantity FROM doctor_warehouse_material_handle as mh inner join doctor_warehouse_sku as s on mh.material_id=s.id WHERE mh.warehouse_id=#{warehouseId}
and mh.warehouse_type=#{warehouseType}
and s.id=#{materialId}
and s.type=#{materialType}
and s.`name` like CONCAT('%', #{materialName}, '%')
AND date_format(settlement_date,'%Y-%m')<=date_format(DATE_SUB(#{settlementDate}, INTERVAL 1 MONTH),'%Y-%m') AND delete_flag=1

mapper.update:
