mapper.update:
UPDATE

SET unit_price=0,amount=0
WHERE farm_id=#{farmId}
AND (type=2 or type=8 or type=9 or type=10 or type=11 or type=12 or type=13)
AND handle_year = #{year} AND handle_month=#{month}
AND delete_flag=1

UPDATE

SET unit_price=0,amount=0
WHERE org_id=#{orgId}
AND (type=2 or type=8 or type=9 or type=10 or type=11 or type=12 or type=13)
AND settlement_date=#{settlementDate}
AND delete_flag=1

mapper.select:
select t1.* from doctor_warehouse_material_handle t1
left join doctor_warehouse_material_apply t2
on t1.id=t2.material_handle_id
where
stock_handle_id=#{stockHandleId}
and delete_flag=1

    AND pig_group_id=#{applyGroupId}


    AND pig_barn_id=#{applyBarnId}

select ifnull(sum(quantity),0) from

where type=13 and delete_flag = 1 and rel_material_handle_id=#{relMaterialHandleId}

    and material_name=#{materialName}

and stock_handle_id!=#{stockHandleId}
order by settlement_date ,id desc;

select distinct material_name from doctor_warehouse_material_handle
where delete_flag=1

    and farm_id=#{farmId}


    and date_format(settlement_date,'%Y-%m') = date_format(CONCAT(#{settlementDate},'-01'),'%Y-%m')


    and warehouse_id=#{warehouseId}

order by id

select '' as id, '上月结存' as material_name, '' as material_type, '' as ware_house_name, '' as handle_date, '' as settlement_date, '' as handler_type, '' as rksl, '' as rkdj, '' as rkje, '' as cksl, '' as ckdj, '' as ckje, sum(a.balance_quantity) as jcsl, sum(a.balance_amount) / sum(a.balance_quantity) as jcdj, sum(a.balance_amount) as jcje, '' as pig_barn_name, '' as pig_type, '' as pig_group_name, '' as apply_staff_name, '' as farm_name, '' as unit, '' as provider_name, '' as specification from doctor_warehouse_stock_monthly a join doctor_warehouse_sku b on a.material_id=b.id where 1=1
and date_format(a.settlement_date,'%Y-%m') = date_format(DATE_SUB(CONCAT(#{settlementDate},'-01'), INTERVAL 1 MONTH),'%Y-%m')
and a.farm_id=#{farmId}
and a.warehouse_id=#{warehouseId}
and b.`name` like CONCAT('%',#{materialName},'%')

select ifnull(b.id,'') as id, ifnull(b.material_name,'') as material_name, ifnull(d.type,'') as material_type, ifnull(a.ware_house_name,'') as ware_house_name, date_format(b.handle_date,'%Y-%m-%d') as handle_date, date_format(b.settlement_date,'%Y-%m') as settlement_date, ifnull(b.type,'') as handler_type, case when b.type in (1,7,9,11) then ifnull(b.quantity,0) else '' end as rksl, case when b.type in (1,7,9,11) then ifnull(b.amount,0) else '' end as rkje, case when b.type in (2,8,10,12,13) then ifnull(b.quantity,0) else '' end as cksl, case when b.type in (2,8,10,12,13) then ifnull(b.amount,0) else '' end as ckje, ifnull(f.pig_barn_name,'') as pig_barn_name, ifnull(f.pig_type,'') as pig_type, ifnull(f.pig_group_name,'') as pig_group_name, ifnull(f.apply_staff_name,'') as apply_staff_name, ifnull(c.`name`,'') as farm_name, ifnull(db.name,'') as unit, ifnull(e.short_name,'') as provider_name, ifnull(d.specification,'') as specification from doctor_warehouse_material_handle b join doctor_ware_houses a on b.warehouse_id=a.id left join doctor_warehouse_material_apply f on b.id=f.material_handle_id
and f.farm_id=#{farmId}
and f.warehouse_id=#{warehouseId}
join doctor_farms c on a.farm_id=c.id join doctor_warehouse_sku d on b.material_id=d.id join doctor_warehouse_vendor e on d.vendor_id=e.id inner join doctor_basics as db on db.id=b.unit where b.delete_flag=1
and b.farm_id=#{farmId}
and date_format(b.settlement_date,'%Y-%m') = date_format(CONCAT(#{settlementDate},'-01'),'%Y-%m')
and b.warehouse_id=#{warehouseId}
and d.`name` like CONCAT('%',#{materialName},'%')
and d.type=#{type}
order by b.handle_date,b.id,f.pig_group_name

select temp.settlement_date as settlementDate,
temp.farm_id as farmId,sum(temp.in_amount)as inAmount,sum(temp.out_amount)as outAmount,sum(temp.balance_amount) as balanceAmount
from
(select  b.settlement_date,b.farm_id, 0 as in_amount,0 as out_amount ,sum(b.balance_amount) as balance_amount
from doctor_warehouse_stock_monthly b
where  b.settlement_date >= #{settlementDateStart}
and  b.settlement_date <= #{settlementDateEnd}
and b.org_id = #{orgId,jdbcType = BIGINT}
GROUP by b.settlement_date,b.farm_id
union all
select c.settlement_date,c.farm_id,sum(c.amount) as in_amount,0 as out_amount,0 as balance_amount
from doctor_warehouse_material_handle c
where
c.delete_flag = 1
and  c.settlement_date >= #{settlementDateStart}
and  c.settlement_date <= #{settlementDateEnd}
and c.org_id = #{orgId,jdbcType = BIGINT}
and c.type in (
1,7,9,11,13
)
GROUP by c.settlement_date,c.farm_id
union all
select d.settlement_date,d.farm_id,0 as in_amount,sum(d.amount) as out_amount,0 as balance_amount
from doctor_warehouse_material_handle d
where
d.delete_flag = 1
and  d.settlement_date >= #{settlementDateStart}
and  d.settlement_date <= #{settlementDateEnd}
and d.org_id = #{orgId,jdbcType = BIGINT}
and d.type in (
2,8,10,12
)
GROUP by d.settlement_date,d.farm_id
) temp
GROUP BY temp.settlement_date,temp.farm_id
order by temp.settlement_date,temp.farm_id
