select dd.pig_id as pigId,dd.pig_code as pigCode,tracks.current_barn_name as
barnName,dd.dpnpd,dd.pfnpd,dd.plnpd,dd.ptnpd,dd.jpnpd,dd.npd
from (
SELECT pig_id,
pig_code,
sum(dp_npd) as dpnpd,
sum(pf_npd) as pfnpd,
sum(pl_npd) as plnpd,
sum(ps_npd) as psnpd,
sum(pt_npd) as ptnpd,
sum(jp_npd) as jpnpd,
