AND farm_name = #{farmName}
AND pig_id = #{pigId}
AND pig_code LIKE concat('%',#{pigCode},'%')
AND is_auto = #{isAuto}
AND  event_at >= #{beginDate}  
AND  event_at <= #{endDate}  
AND type = #{type}
GROUP BY pig_id,pig_code
) dd , `doctor_pig_tracks` tracks
WHERE
dd.pig_id = tracks.`pig_id`
AND tracks.current_barn_id = #{barnId}
AND tracks.current_barn_name = #{barnName}
limit #{offset}, #{limit}

