barn_name AS barnName,
event_at AS salesDate,
quantity AS pigNumber,
pig_type_name AS pigTypeName,
weight AS pigWeight,
price AS pigPrice,
day_age AS ages,
amount AS pigAmount,
customer_name AS client
FROM v_doctor_sales
WHERE 1 =1
AND farm_id = #{farmId}
AND batch_no = #{batch}
AND barn_id = #{barnId}
AND barn_name = #{barnName}
AND pig_type = #{pigTypeId}
