  select t5.pig_id, t5.pig_code,t5.event_at,t5.max_event_at_id,t5.barn_name,t5.barn_id,t5.source,t5.breed_name,t5.breed_id,t5.`status`,
      t5.in_farm_date, t5.out_farm_date,f.staff_name from doctor_barns f,
    (select t4.pig_id, t4.pig_code,t4.event_at,t4.max_event_at_id,t4.barn_name,t4.barn_id,t4.source,t4.breed_name,t4.breed_id,e.`status`,
           t4.in_farm_date , e.out_farm_date from doctor_pig_tracks e,
         (select t3.pig_id, t3.pig_code,t3.event_at,t3.max_event_at_id,t3.barn_name,t3.barn_id,t3.source,t3.breed_name,t3.breed_id,
               d.in_farm_date  from doctor_pigs d,
            (select c.pig_id, c.pig_code,c.event_at,t2.max_event_at_id,c.barn_name,c.barn_id,c.source,c.breed_name,c.breed_id
                 from  doctor_pig_events c,
              (select b.pig_id, max(b.id) as max_event_at_id from doctor_pig_events b,
                (select a.pig_id, max(a.event_at) as max_event_at from doctor_pig_events a
                where a.farm_id = #{farmId} and a.kind = 2 and a.`status` = 1 and a.event_at <= #{queryDate}
                GROUP BY a.pig_id
                 ) t1
                where b.pig_id = t1.pig_id and b.farm_id = #{farmId} and b.kind = 2 and b.`status` = 1 and b.event_at <= #{queryDate}
                GROUP BY b.pig_id
              ) t2
                where c.pig_id = t2.pig_id and c.id = t2.max_event_at_id and c.type <> 6
             ) t3
            where d.id = t3.pig_id
        )t4
        where e.pig_id = t4.pig_id
    ) t5
    where f.id = t5.barn_id
  ) t6
where
1 = 1
 and t6.pig_code like CONCAT(CONCAT('%',#{pigCode}),'%')
 and t6.breed_id = #{barnId} 
 and t6.breed_id = #{breedId} 
 and t6.staff_name = #{staffName} 
 and t6.status = #{pigStatus} 
 and t6.in_farm_date > #{beginDate} 
 and t6.out_farm_date < #{endDate}
