mapper.select:
select count(1)
from doctor_pig_events c,
(select b.pig_id, max(b.id) as max_event_id
from doctor_pig_events b,
(select a.pig_id, max(a.event_at) as max_event_at from doctor_pig_events a
where a.org_id=#{orgId} and a.farm_id = #{farmId} and a.kind = 1 and a.`status` = 1 and a.event_at <= #{sumAt}
group by a.pig_id) t1
where b.pig_id = t1.pig_id and b.event_at = t1.max_event_at and b.farm_id = #{farmId} and b.org_id=#{orgId}
and b.kind = 1 and b.`status` = 1 and b.event_at <= #{sumAt}
group by b.pig_id
) t2
where c.pig_id = t2.pig_id and c.id = t2.max_event_id
and c.type != 2 and c.type != 6 and c.type != 12
and (((c.type = 1 and c.barn_type = 7) or c.type = 14)
or (c.type != 1 and c.type != 14 and (c.barn_type = 7)))

 select count(1)
from doctor_pig_events c,
(select b.pig_id, max(b.id) as max_event_id
from doctor_pig_events b,
(select a.pig_id, max(a.event_at) as max_event_at from doctor_pig_events a
where a.org_id=#{orgId} and a.farm_id = #{farmId} and a.kind = 1 and a.`status` = 1 and a.event_at <= #{sumAt}
group by a.pig_id) t1
where b.pig_id = t1.pig_id and b.event_at = t1.max_event_at and b.farm_id = #{farmId} and b.org_id=#{orgId}
and b.kind = 1 and b.`status` = 1 and b.event_at <= #{sumAt}
group by b.pig_id
) t2
where c.pig_id = t2.pig_id and c.id = t2.max_event_id
and c.type != 2 and c.type != 6 and c.type != 14
and (((c.type = 1 and c.barn_type != 7) or c.type = 12)
or (c.type != 1 and c.type != 12 and (c.barn_type != 7)))

SELECT ( SELECT count(1) FROM doctor_pigs WHERE is_removal = 0 AND pig_type = 2 AND farm_id = #{farmId} AND boar_type = 1 ) - ( SELECT ifnull(sum( case when `type` in (2,6) then -1 when `type` in (7,20) then 1 else 0 END ),0) FROM doctor_pig_events
farm_id = #{farmId} AND event_at > #{sumAt} AND `type` IN (2, 6, 7, 20) AND kind = 2 AND boar_type = 1 AND status = 1 AND event_source != 5
) FROM dual

SELECT count(*) from (
SELECT pig_id from doctor_pig_events
where farm_id=#{farmId} and kind=1 and type=#{type}
and event_at = #{sumAt}
GROUP BY pig_id
) as t
